mapper.select:
SELECT

FROM ts_user a

	
		AND a.id = #{query.id, jdbcType=BIGINT }
	
	
		AND a.username = #{query.username, jdbcType=VARCHAR }
	
	
		AND a.password = #{query.password, jdbcType=VARCHAR }
	
	
		AND a.salt = #{query.salt, jdbcType=VARCHAR }
	
	
		AND a.nickname = #{query.nickname, jdbcType=VARCHAR }
	
	
		AND a.mobile = #{query.mobile, jdbcType=VARCHAR }
	
	
		AND a.depart_id = #{query.departId, jdbcType=BIGINT }
	
	
		AND a.org_id = #{query.orgId, jdbcType=BIGINT }
	
	
		AND a.status = #{query.status, jdbcType=TINYINT }
	

ORDER BY a.id DESC

SELECT t1.* from ts_user t1 left join ts_user_role t2 on t1.id = t2.user_id
WHERE t1.deleted = 0 and t2.deleted = 0
AND
t2.role_id = #{roleId}

	AND t1.username = #{username}


	AND t1.mobile = #{mobile}


	AND t1.gender = #{gender}
